sections.css:
$color-primary: #334499;
$color-alert: #dd3333;
$color-text-main: #404040;



/* Base Font size */
$bf: 14px;
$bf-large: 16px;


/* Some set breakpoints - these are not THE breakpoints - content dictates breakpoints! */
/* These are just some breakpoints that we know we will use throughout the site */
$bp-small: 720px;

/* I am styling on a body class as well as the body tag - this is a workaround because i cannot append styles to the body tag in the styleguide */
body, .sgwa-body {
  font-size: $bf;

  @media (min-width: $bp-small) {
    font-size: $bf-large;
  }
}


* {
  font-family: 'Merriweather', serif;
  font-size: 1em;
  line-height: 1.5;
  color: $color-text-main;
  margin: 0;

  &.color-primary {
    color: $color-primary;
  }
}

.text {
  &--small {
    font-size: 0.6em;
  }
}


.header {
  font-family: 'Raleway', sans-serif;
  line-height: 1.2;
  margin-top: 0;

  &--large {
    @extend .header;
    font-size: 2.5em;
    margin-bottom: 1em;
  }

  &--medium {
    @extend .header;
    font-size: 2em;
    margin-bottom: 0.75em;
  }

  &--small {
    @extend .header;
    font-size: 1.5em;
    margin-bottom: 0.5em;
  }

  &--smaller {
    @extend .header;
    font-size: 1.2em;
    margin-bottom: 0.5em;
  }
}

.spot-example {
  border: 1px solid black;
  display: inline-block;
}

.spot-example--rounded {
  border-radius: 20px;
  overflow: hidden;
}

.spot-example--aggressive {
  border-color: $color-alert;
  border-width: 5px;
}

.test {
  border: 1px solid black;
  display: inline-block;
}

.test--rounded {
  border-radius: 20px;
  overflow: hidden;
}

.test--aggressive {
  border-color: $color-alert;
  border-width: 5px;
}

